INSERT INTO DEFINIZIONE VALUES
(1, 'Norma euclidea', 'Sia $ \vec{x} \in\mathbb{R}^{n} $, si definisce la sua norma euclidea come $ \parallel\vec{x}\parallel = \sqrt {\sum^{n}_{i=0}x^2_{i}} $.', 1, 1),
(2, 'Intorno sferico', 'Sia r > 0, $ \vec{x} \in\mathbb{R}^{n} $, definiamo il suo intorno sferico come B$_{r}\left( \vec{x}\right) = \left\{\vec{y}\in \mathbb{R}^{n}:\left\|\vec{x}-\vec{y}\right\| < r\right\}$.', 1, 1),
(3, 'Punto di accumulazione', 'Dato un insieme E$ \subseteq \mathbb{R} $, $ \vec{x} \in\mathbb{R}^{n} $ è punto di accumulazione di E se $ \forall\epsilon>0\ \exists\vec{y}\in $ E $ ,\ \vec{y}\neq\vec{x}:\ \parallel\vec{x}-\vec{y}\parallel<\epsilon $.', 1, 1),
(4, 'Punto isolato', 'Dato un insieme $ \mathbb{E} \subseteq \mathbb{R} $, $ \vec{x}\in\mathbb{E} $ è un punto isolato per $ \mathbb{E} $ se NON è un suo $\href{def(3)}{\text{punto di accumulazione}}$.', 1, 1),
(17, 'Derivata parziale', 'Sia f:X$ \subseteq\mathbb{R}^{n}\rightarrow\mathbb{R} $, v=e$ _k $=(0,0,...,0,1,0,...,0) $ \forall k=0,1,2,...,n $ versore di $ \mathbb{R}^{n} $, allora D$ _{{e}_{k}} = \frac{\partial f(\vec{x})}{\partial e_k}=f_{{e}_k}(\vec{x}) $ si dice derivata parziale di f rispetto a $ \vec{x} $.', 5, 1),
(5, 'Distanza euclidea', 'Siano $ \vec{x} $ e $ \vec{y} \in\mathbb{R}^{n} $, si definisce la distanza euclidea d($\vec{x},\vec{y}$) = $\parallel\vec{x}-\vec{y}\parallel$.', 1, 1),
(8, 'Insieme aperto', 'Un insieme E$ \subseteq\mathbb{R}^{n} $ è aperto se $ \forall\vec{x}\in $E $ \exists $U $\href{def(2)}{\text{intorno}} $ di $ \vec{x} $ vale che U$ \subseteq $E.', 1, 1),
(18, 'Gradiente', 'Sia f:X$ \subseteq\mathbb{R}^{n}\rightarrow\mathbb{R} $, $ \vec{x}\in\dot{X} $, se $ \exists\href{def(17)}{\frac{\partial f(\vec{x})}{\partial x_k}}\ \forall k=0,1,2,...,n $ f si dice derivabile in $ \vec{x} $ e definiamo il gradiente di f in $ \vec{x} $ come $ \href{def(1000)}{\triangledown} f\left(\vec{x}\right)\doteq(\frac{\partial f(\vec{x})}{\partial x_1}, \frac{\partial f(\vec{x})}{\partial x_2}, ... , \frac{\partial f(\vec{x})}{\partial x_n}) $.', 5, 1),
(19, 'Differenziabilita di una funzione scalare', 'Sia f:X $ \subseteq\mathbb{R}^{n}\rightarrow\mathbb{R} $, f è differenziabile in $ \vec{x}\in\dot{X} $ se $ \exists\vec{a}\in\mathbb{R}^{n}: $ f($ \vec{x} $) = f$ (\vec{x_0}) + \vec{a}\cdot(\vec{x}-\vec{x_0})+ $o($\parallel\vec{x}-\vec{x_0}\parallel$), per $ \vec{x}\rightarrow\vec{x_0} $.', 5, 1),
(1000, 'Nabla', 'Definiamo Nabla come un vettore in $ \mathbb{R}^{n} $ di $ \href{def(17)}{\text{derivate parziali}} $, $ \triangledown\doteq\left(\frac{\partial}{\partial x_1}, \frac{\partial}{\partial x_2}, ... , \frac{\partial}{\partial x_n}\right) $.', 5, 1),
(20, 'Piano tangente', 'Sia f:X $ \subseteq\mathbb{R}^{n}\rightarrow\mathbb{R} $, f $ \href{def(19)}{\text{differenziabile}} $ in X, sia $ \vec{x} $ =(x,y) $ \in $X, la miglior approssimazione di f in $ \vec{x} $ è g(x,y)=f(x$ _0 $, y$ _0 $)+$\href{def(17)}{\frac{\partial f( x_0 ,  y_0 )}{\partial x}}$(x-x$ _0 $)+$\frac{\partial f(x_0 , y_0 )}{\partial y}$(y-y$_0$), con graf(g) piano in $ \mathbb{R}^{3} $ passante per (x$ _0$,y$ _0 $, f(x$ _0$,y$ _0 $))$ \in\mathbb{R}^{3} $ di equazione z-$\frac{\partial f(x_0,y_0)}{\partial x}$(x-x$ _0 $)-$\frac{\partial f(x_0,y_0)}{\partial y}$(y-y$ _0$) = f(x$ _0 $,y$ _0 $).', 5, 1),
(30, 'Matrice Jacobiana', 'Sia $ \vec{x_0}\in\mathbb{R}^{n} $, la matrice Jacobiana in $ \vec{x_0} $ è M($ \vec{x_0} ) = \left(\begin{array}{c} \href{def(18)}{\triangledown f_1(\vec{x_0})}\\ \triangledown f_2(\vec{x_0})\\ ...\\ \triangledown f_m(\vec{x_0})\end{array}\right)$.', 5, 1),
(29, 'Differenziabilita di una funzione vettoriale', 'Sia $ \vec{x_0}\in\mathbb{R}^{n} $, la matrice Jacobiana in $ \vec{x_0} $Sia $ \vec{x_0}\in\mathbb{R}^{n} $, Sia $ \vec{f} $:X$ \subseteq\mathbb{R}^{n}\rightarrow\mathbb{R}^{m} $, $ \vec{f} $ è differenziabile in $ \vec{x}\in\dot{X} $ se $ \exists$M$_{m\times{n}} $ matrice : $\vec{f}$($ \vec{x} $)=$ \vec{f} (\vec{x_0}) + M\cdot(\vec{x}-\vec{x_0})+ $o($\parallel\vec{x}-\vec{x_0}\parallel$), per $ \vec{x}\rightarrow\vec{x_0} $, dove M è la $\href{def(30)}{\text{matrice Jacobiana}}$ di $ \vec{f} $.. Sia $ \vec{f} $:X$ \subseteq\mathbb{R}^{n}\rightarrow\mathbb{R}^{m} $, $ \vec{f} $ è differenziabile in $ \vec{x}\in\dot{X} $ se $ \exists$M$_{m\times{n}} $ matrice : $\vec{f}$($ \vec{x} $)=$ \vec{f} (\vec{x_0}) + M\cdot(\vec{x}-\vec{x_0})+ $o($\parallel\vec{x}-\vec{x_0}\parallel$), per $ \vec{x}\rightarrow\vec{x_0} $, dove M è la $\href{def(30)}{\text{matrice Jacobiana}}$ di $ \vec{f} $.', 9, 1),
(1001, 'Funzione limitata', 'Sia f:X$ \subseteq\mathbb{R}^{n}\rightarrow\mathbb{R} $, definiamo la funzione f limitata a U$ \subseteq $X come f$ \mid_U: $U$ \rightarrow\mathbb{R} $.', 4, 1),
(1002, 'Insieme convesso', 'A $ \subseteq\mathbb{R}^{n} $ è un insieme convesso se $ \forall\vec{x},\vec{y}\in $A, $ \left\{(1-t)\vec{x}+t\vec{y}\ :\ 0\leq t\leq1\right\}\subseteq $A.', 7 ,1),
(1003, 'Funzione convessa', 'Dato A$ \subseteq\mathbb{R}^{n} $, f:A$ \rightarrow\mathbb{R} $ si dice convessa se $ \forall\vec{x},\vec{y}\in $A : f( (1-t)$ \vec{x}-t\vec{y} $ )$\ \leq\ $(1-t)f($ \vec{x} $) + tf($ \vec{y} $) $ \forall $t$ \in(0,1)$, inoltre è strettamente connessa se vale il < nella disuguaglianza sopra.', 7, 1),
(1004, 'Funzione concava', 'Una funzione f è concava se non è $ \href{def(1003)}{\text{convessa}} $.', 7,1),
(1005, 'Punto critico', 'Sia f $ \href{def(19)}{\text{differenziabile}} $ in $ \vec{x_0}\in $Dom(f) : $ \href{def(18)}{\triangledown f(\vec{x_0})} $=0 $ \Rightarrow $ $ \vec{x_0} $ è punto critico o stazionario di f', 8, 1),
(23, 'Doppia differenziabilità', 'Si dice che f è due volte differenziabile in $ \vec{x} $ se è $ \href{def(19)}{\text{differenziabile}} $ in U $ \href{def(2)}{\text{intorno}} $ di $ \vec{x} $ e tutte le $ \href{def(17)}{\text{derivate parziali}} $ f$ _{x_1} $, f$ _{x_2} $, ... , f$ _{x_n} $ sono a loro volta differenziabili in $ \vec{x} $.', 5, 1),
(1006, 'Matrice Hessiana', 'Sia f:X$ \subseteq\mathbb{R}^2\rightarrow\mathbb{R} $, definisco la matrice Hessiana di ordine 2: $ \\ $ D$ ^{2} $f($ \vec{x} $) $ \doteq\ \begin{pmatrix}\href{def(18)}{ \triangledown f_x(\vec{x})} \\ \triangledown f_y(\vec{x}) \end{pmatrix}$ = $ \begin{pmatrix} \frac{\partial }{\partial x} \left( \frac{\partial f}{\partial x}(\vec{x})\right) & \frac{\partial }{\partial y} \left( \frac{\partial f}{\partial x}(\vec{x})\right) \\ \frac{\partial }{\partial x} \left( \frac{\partial f}{\partial y}(\vec{x})\right) & \frac{\partial }{\partial y} \left( \frac{\partial f}{\partial y}(\vec{x})\right) \end{pmatrix} $.', 5, 1),
(28, 'Forme definite e semidefinite positive e negative', 'Data una matrice simmetrica A $ _{n\times{n}} $ e la sua forma quadratica Q($ \vec{v} $) = $ \vec{v}^{\text{T}}\cdot $A$ \cdot\vec{v} $, con $ \vec{v}\in\mathbb{R}^{n} $, vale che: $ \\ $$ \textbf{1)} $ D$ ^2 $f($ \vec{x} $) definita positiva se Q($ \vec{v} $)>0, definita negativa se Q($ \vec{v} $)<0, $ \forall\vec{v}\in\mathbb{R}^{n}; $$ \\ $$ \textbf{2)} $ D$ ^2 $f($ \vec{x} $) semidefinita positiva se Q($ \vec{v} $)$ \geq $0, definita negativa se Q($ \vec{v} $)$ \leq $0, $ \forall\vec{v}\in\mathbb{R}^{n} $;$ \\ $$ \textbf{3)} $ D$ ^2 $f($ \vec{x} $) indefinita se $ \exists\vec{v_1}, \vec{v_2}\in\mathbb{R}^{n}: $ Q($ \vec{v_1} $)>0 $ \wedge $ Q($ \vec{v_2} $)<0.', 6, 1),
(14, 'Curva', 'Si dice curva una funzione continua del tipo $ \vec{\gamma} $: I$ \subseteq\mathbb{R}\rightarrow\mathbb{R}^{n} $', 3, 2),
(1007, 'Funzioni di classe $ C^1 $', 'Sia f: X$ \subseteq\mathbb{R}^{n}\rightarrow\mathbb{R} $, sia E$ \subseteq $X. Dico che f $ \in C^1( $E) se f e $ \href{def(17)}{\frac{\partial f}{\partial x}} \in C( $E)', 5,1),
(1008, 'Insieme chiuso', 'Un insieme E$ \subseteq\mathbb{R}^{n} $ è chiuso se E$ ^c $ è $ \href{def(8)}{\text{aperto}} $.', 1, 1),
(1009, 'Frontiera e chiusura di un insieme', 'Dato E$ \subseteq\mathbb{R}^{n} $, definiamo come $ \partial $ E la frontiera di E, cioè i punti di E che non sono suoi punti interni nè esterni. Inoltre la chiusura di E è $ \overline{\text{E}}\doteq\text{E}\ \cup\partial\text{E} $, che coincide con E se questo è chiuso.', 1, 1),
(52, 'Insiemi x e y-semplici', 'Un insieme E$ \subseteq\mathbb{R}^2 $ è y-semplice se $ \exists\ \text{g}_1 $(x), g$ _2 $(x)$ \in C^1 $([a,b]) tali che E={(x,y): c$ \leq $ x $ \leq $d, g$ _1 $(x)$ \leq $ y $ \leq $g$ _2 $(x)}. La stessa cosa vale, in maniera simmetrica, per gli insiemi x-semplici.', 16, 3),
(39, 'Baricentro di una curva', 'Sia $ \vec{\gamma} $: [a,b]$ \subseteq\mathbb{R}\rightarrow\mathbb{R}^3 $ una $ \href{def(14)}{\text{curva}} $, $ \mu $(x,y,z):$ \ \gamma([a,b])\rightarrow\mathbb{R} $ ($ \mu\equiv 1 $ in assenza di specifiche), M = $ \int_{\vec{\gamma}}\ \mu $(x,y,z) dxdydz, allora definiamo come baricentro il punto $ \vec{B}\in\mathbb{R}^3 $: $ \\ $ $ \vec{B}\doteq $ ($ \frac{1}{M}\int_{\vec{\gamma}} $ x$ \mu $(x,y,z) dxdydz, $ \frac{1}{M}\int_{\vec{\gamma}} $ y$ \mu $(x,y,z) dxdydz, $ \frac{1}{M}\int_{\vec{\gamma}} $ z$ \mu $(x,y,z) dxdydz).', 13, 2),
(1010, 'Equazione differenziale', 'Un''equazione differenziale di ordine m è del tipo $ F(\ t,\ y(t),\ y^{(1)},\cdot \cdot \cdot,\ y^{(m)})=0 $, nell''incognita $ y:I\subseteq\mathbb{R}\rightarrow\mathbb{R} $ e con $ A:\text{X}\subseteq\mathbb{R}^{m+2}\rightarrow\mathbb{R} $.', 23,5),
(1011, 'Equazione differenziale omogenea', 'Data un''$ \href{def(1010)}{\text{equazione differenziale}} $, per ottenere l''equazione differenziale ad essa associata va tolto il termine noto $ a(t) $.', 23, 5),
(1012, 'Numero complesso', 'Un numero $ z_0 $ è complesso, $ z_0\in\mathbb{C} $, se vale che $ z_0=\alpha+i\beta $, dove $ \alpha=Re(z_0), \beta=Im(z_0)\ \in\ \mathbb{R} $ e $ i $, detto immaginario puro, è tale che $ i^2=-1 $.', 29, 6);
