INSERT INTO TEOREMA VALUES
(1, 'proposizione', 'Disuguaglianza di Cauchy-Schwarz', 'Siano $ \vec{x},\ \vec{y}\in\mathbb{R}^{n} $','allora vale che $ \mid\vec{x}\cdot\vec{y}\mid\leq\left\|\vec{x}\right\|\cdot\left\|\vec{y}\right\| $', null, 1, 1),
(2, 'teorema', 'Disuguaglianza triangolare', 'Siano $ \vec{x} $ e $ \vec{y} \in\mathbb{R}^{n} $', 'Allora $ \parallel\vec{x}+\vec{y}\parallel\leq\parallel\vec{x}\parallel+\parallel\vec{y}\parallel $', null, 1, 1),
(18, 'teorema', 'Condizioni sufficienti di estremo locale', 'Sia f:A$ \rightarrow\mathbb{R} $, A $ \subseteq\mathbb{R}^{n}\ \href{def(8)}{\text{aperto}} $, sia $ \vec{x_0}\in $A $ \href{def(1005)}{\text{punto critico}} $ di f, U $ \href{def(2)}{\text{intorno}} $ di $ \vec{x_0} $.', 'Allora: $ \\ $$\textbf{1)}$ $ \href{def(1001)}{\text{f}\mid_U} $ (strettamente) $ \href{def(1003)}{\text{convessa}} \Rightarrow \vec{x_0} $ è punto di minimo locale (forte) di f; $ \\ $$\textbf{2)}$ f$ \mid_U $ (strettamente) $ \href{1004}{\text{concava}} \Rightarrow \vec{x_0} $ è punto di massimo locale (forte) di f. $ \\ $$ \\ $Inoltre, se f è $ \href{def(23)}{\text{2 volte differenziabile}} $ in $ \vec{x_0} $, Allora vale che: $ \\ $$\textbf{3)}$ $ \href{def(1006)}{\text{matrice Hessiana}} $ D$ ^2 $f($ \vec{x_0} $)  $ \href{def()}{\text{definita positiva}} \Rightarrow\ \vec{x_0} $ punto di minimo forte,$ \\ $$\textbf{4)}$ D$ ^2 $f($ \vec{x_0} $) definita negativa $ \Rightarrow $ $ \vec{x_0} $ è punto di massimo forte,$ \\ $$\textbf{5)}$ D$ ^2 $f($ \vec{x_0} $) indefinita $ \Rightarrow\ \vec{x_0} $ è punto di sella, $ \\ $$\textbf{6)}$ D$ ^2 $f($ \vec{x_0} $) semidefinita positiva o negativa $ \Rightarrow\ $ non si hanno abbastanza informazioni per classificare a priori la natura di $ \vec{x_0} $.', null, 8, 1),
(17, 'teorema', 'Fermat', 'Sia f:$ \mathbb{R}^{n}\rightarrow\mathbb{R} $, A $ \href{def(8)}{\text{aperto}},\ \vec{x_0} $ punto di estremo locale di A e f $ \href{def(18)}{\text{derivabile}} $ in $ \vec{x_0} $','$ \\ $ $ \Rightarrow\ \triangledown f(\vec{x_0})$ = 0.', null, 8, 1),
(25, 'teorema','Rettificabilità delle curve di classe $ C^1 $', 'Sia $ \vec{\gamma} $ è una curva di classe $ C^1 $', 'Allora $ \vec{\gamma} $ è rettificabile, inoltre la sua lunghezza si esprime con L($ \vec{\gamma} $)=$  \int_a^b \left\|\vec{\gamma}(t)\right\|dt $', null, 13, 2),
(62, 'teorema','Pappo Guldino', 'Sia A$ \subset\text{S}\subseteq\mathbb{R}^2 $ dove S è un semipiano delimitato dalla retta $ r\in\mathbb{R}^3 $', 'Allora il volume del solido generato dalla rotazione di $ \text{A} $ attorno a $ r $, cioè $ \text{R(A)} $, è $ \\ $ V$ \left( \text{R(A)}\right)=|\text{A}|_{2}\cdot L(\vec{B}_{\gamma}) $,$ \\ $ dove $ |\text{A}|_{2} $ è inteso come area di A e $ L(\vec{B}_{\gamma}) $ è la lunghezza della curva percorsa dal baricentro di $ \gamma $ nella rotazione.', null, 17, 3), 
(70, 'teorema', 'Principio di sovrapposizione', 'Consideriamo un''$ \href{def(1010)}{\text{equazione differenziale}} $.', 'Il suo integrale generale si ottiene sommando l''integrale generale dell''$ \href{def(1011)}{\text{equazione omogenea}}\ z(t) $ ad essa associata a una soluzione particolare $ \widetilde{y}(t) $. Quindi $ y(t)=z(t)+\widetilde{y}(t). $', null, 5, 1),
(74, 'teorema', 'Caratterizzazione della derivabilità in senso complesso mediante le condizioni di Cauchy-Riemann', 'Sia $ f:A\subseteq\mathbb{C}\rightarrow\mathbb{C} $ del tipo $ f\left(x+i\ y\right)=u(x,y)+i\ v(x,y) $, con $ u,v:X\subseteq\mathbb{R}^2\rightarrow\mathbb{R} $, sia $ \href{def(1012)}{\text{$\mathbb{C}\ni z_0$}} =x+i\ y $.', '$ f $ derivabile in senso complesso in $ z_0\ \Leftrightarrow\ u,v\ \href{def(19)}{\text{differenziabili}} $ in $ (x_0,y_0) $ e valgono le condizioni di Cauchy-Riemann: \begin{cases}\href{def(17)}{\text{$ \frac{\partial}{\partial x} $}} u=\frac{\partial}{\partial y} v \\\frac{\partial}{\partial y} u=-\frac{\partial}{\partial x} v\end{cases}', null, 31, 6);
